import sys
n = int(sys.stdin.readline())
heights = list(map(int,sys.stdin.readline().split()))
jump = [1]*n

for i in range(n):
    for j in range(i+1,n):
        if heights[i]<heights[j]:
            jump[j] = max(jump[i]+1, jump[j])
print(max(jump))

